mapper.select:

where (`table`.status = 0 or `table`.user_id = #{userId})
and `user`.status = 0
order by `table`.create_time DESC limit #{startIndex}, #{pageSize};


where `table`.id = #{id}
and (`table`.status = 0 or `table`.user_id = #{userId})
and `user`.status = 0
group by `table`.id;

mapper.delete:
delete
from `support`
where table_id = #{tableId}
  and user_id = #{userId};

mapper.select:
select count(1)
from `support`
where table_id = #{tableId}
  and user_id = #{userId};
