mapper.delete:
delete
from `support`
where table_id = #{tableId}
  and user_id = #{userId};

mapper.insert:
insert into `table` (user_id, anonymous, sender, sender_sex, recipient,
                     recipient_sex, create_time, content, images)
    value (#{userId},#{anonymous},#{sender},#{senderSex},#{recipient},#{recipientSex},now(),#{content},#{images});
